import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import SendIcon from '@mui/icons-material/Send';
import { useEffect, useState } from 'react';
import { Button, CardActionArea, CardActions } from '@mui/material';
import axios from 'axios';

// Container component to hold the cards in a row
const CoursesRow = () => {
  const [courses, setCourses] = useState([]);  // State initialized as an empty array
  // const [link, setLink] = useState('')
  // const [title, setTitle] = useState('')
  // const [description, setDescription] = useState('')

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:4200/showCourses');
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        const data = await response.json();
        setCourses(data.courses);
        console.log(data.courses); // Log updated courses
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();

  }, []);

  return (
    <div style={{ display: 'flex' , justifyContent: 'space-between',flexWrap: 'wrap' }}>
      {Array.isArray(courses) && courses.length > 0 && courses.map((course, index) => (
        <Courses
          title={course.name}
          description={course.description}
          image={course.image}
        />)
      )}
    </div>
  );
};

const Courses = ({ title, description, image }) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="200"
          image={image}
          alt={title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary" startIcon={<SendIcon />}>
          enroll now
        </Button>
      </CardActions>
    </Card>
  );
};
export default CoursesRow;
