import React, { useEffect, useState } from 'react'
import './register.css'
import { useNavigate } from 'react-router-dom'
import RegisterDetails from './registerDetails'

export default function Register() {
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [reEnterPassword, setRePassword] = useState("")
  const navigate = useNavigate()

  useEffect(()=>
  {
    const auth = localStorage.getItem('student');
    if(auth)
    {
      render(<RegisterDetails/>)
      
    }
  },[])

  const collectData = async () => {
    if (password.match(reEnterPassword)) {
      let result = await fetch('http://localhost:5500/register', {
        method: 'post',
        body: JSON.stringify({ email, password }),
        headers: {
          'content-type': 'application/json'
        },
      })

      result = await result.json()
      console.log(result)

      localStorage.setItem("student",JSON.stringify(result))
      navigate('/planner')
    }
    else
    {
      alert('Re-entered password and password are not same try again')
    }

  }
  return (
    <div className='registerPage'>
      <h1>Crack Your Goal with India's top educator</h1>
      <input className='inputBox' type='text' placeholder='Enter your Email' value={email} onChange={(e) => setEmail(e.target.value)} /><br></br>

      <input className='inputBox' type='password' placeholder='Enter Password' value={password} onChange={(e) => setPassword(e.target.value)} /><br></br>

      <input className='inputBox' type='password' placeholder='Re-enter password' value={reEnterPassword} onChange={(e) => setRePassword(e.target.value)} /><br></br>

      <button onClick={collectData} type='button'>Sign up</button>
    </div>
  )
}
