import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import SendIcon from '@mui/icons-material/Send';
import { useEffect, useState } from 'react';

import { Button, CardActionArea, CardActions } from '@mui/material';
import axios from 'axios';

// Container component to hold the cards in a row
const CoursesRow = () => {
  const [Courses1, setCourses] = useState('')
  useEffect(() => {
    // let data = await fetch('http://localhost:4200/showCourses', {
    //   method: 'get',
    //   body: JSON.stringify({ Courses1}),
    //   headers: {
    //     'content-type': 'application/json'
    //   },
    // })
    fetchData()
  }, [])

  const fetchData = async () => {
    const data = await axios.get("http://localhost:4200/showCourses")
    setCourses(data)
  }

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
      {
        Courses1.map((course) => 
          <Courses
            title={course.name}
            description="Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging across all continents except Antarctica."
            image={course.link}
          />
        )
      }

      {/* <Courses
        title="Data Science"
        description="Data science is an interdisciplinary field that uses scientific methods, processes, algorithms, and systems to extract knowledge and insights from structured and unstructured data."
        image="/Assets/ui-ux.png"
      />
      <Courses
        title="Mobile Development"
        description="Mobile app development is the act or process by which a mobile app is developed for mobile devices, such as personal digital assistants, enterprise digital assistants, or mobile phones."
        image="/Assets/graphics-design.png"
      /> */}
    </div>
  );
};

const Courses = ({ title, description, image }) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image={image}
          alt={title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary" startIcon={<SendIcon />}>
          enroll now
        </Button>
      </CardActions>
    </Card>
  );
}

export default CoursesRow;
