import { React, useState,useEffect } from 'react'
import './login.css'
import { useNavigate } from 'react-router-dom'
const bcrypt = require('bcrypt')

export default function Login() {
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const navigate = useNavigate()

  useEffect(()=>
  {
    const auth = localStorage.getItem('student');
    if(auth)
    {
      navigate('/')
    }
  },[])

  const handleLogin = async () => {
    password = await bcrypt.hash(password)
    let result = await fetch('http://localhost:5000/login', {
      method: 'post',
      body: JSON.stringify({ email, password }),
      headers: {
        'content-type': 'application/json'
      },
    })

    result = await result.json()
    if(result.email)
    {
      localStorage.setItem("student", result.stringify)
      navigate('/planner')
    }
    else
    {
      alert("Please enter correct details")
    }
    
  }

  return (
    <div className='loginPage'>
      <input className='inputBox' type='text' placeholder='Enter your Email' value={email} onChange={(e) => setEmail(e.target.value)} /><br></br>

      <input className='inputBox' type='password' placeholder='Enter Password' value={password} onChange={(e) => setPassword(e.target.value)} /><br></br>

      <button onClick={handleLogin} type='button'>Log in</button>
    </div>
  )
}
