import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import SendIcon from '@mui/icons-material/Send';
import { useEffect, useState } from 'react';
import { Button, CardActionArea, CardActions } from '@mui/material';
import axios from 'axios';

// Container component to hold the cards in a row
const CoursesRow = () => {
  const [courses, setCourses] = useState([]); // State initialized as an empty array

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    //   try {
    //     const response = await axios.get("http://localhost:4200/showCourses");
    //     if (Array.isArray(response.data)) {
    //       setCourses(response.data); // Assuming your courses array is directly in response.data
    //     } else {
    //       console.error('Unexpected data format:', response.data);
    //     }
    //   } catch (error) {
    //     console.error('Error fetching courses:', error);
    //   }
    // };
    let result = await fetch('http://localhost:4200/showCourses')
    result = await result.json()
    setCourses(result)
  }

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
      {courses.map((course, index) => 
        <Courses
          key={index}
          title={course.name}
          description={course.description}
          image={course.link}
        />
      )}
    </div>
  );
};

const Courses = ({ title, description, image }) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image={image}
          alt={title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary" startIcon={<SendIcon />}>
          enroll now
        </Button>
      </CardActions>
    </Card>
  );
};
export default CoursesRow;
