import React, { useState } from 'react';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import './nav.css';
import logoImage from '../assets/eLearning.webp'; // Import the logo image

const Nav = () => {
  const isLoggedIn = !!localStorage.getItem('student');
  const navigate = useNavigate();
  const { pathname } = useLocation();

  const [darkMode, setDarkMode] = useState(false); // State variable for dark mode

  const toggleDarkMode = () => {
    setDarkMode(!darkMode); // Toggle between light and dark mode
  };

  const logout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <nav className={`navbar ${darkMode ? 'dark-mode' : ''}`}>
      <div className="container">
        <div className="logo">
          <img src={logoImage} alt="Logo" className="logo-image" /> {/* Use the logo image */}
          <span className="website-name">Learner's</span> {/* Include the website name */}
        </div>
        <ul className="nav-menu">
          {isLoggedIn ? (
            <>
              {/* Other menu items */}
              <li><button onClick={logout}>Logout</button></li>
            </>
          ) : (
            <>
              <li><button><NavLink to='/login' activeClassName="active">Log in</NavLink></button></li>
              <li><button><NavLink to='/signup' activeClassName="active">Sign up</NavLink></button></li>
            </>
          )}
          <li>
            {/* Toggle button for dark mode */}
            <button onClick={toggleDarkMode}>{darkMode ? 'Light Mode' : 'Dark Mode'}</button>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Nav;
