import React, { useState, useEffect } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import SendIcon from '@mui/icons-material/Send';
import { useNavigate } from 'react-router-dom';
import { Button, CardActionArea, CardActions } from '@mui/material';

const CoursesRow = () => {
    const [enrolledCourses, setEnrolledCourses] = useState([]);
    
    // Fetching student details from localStorage
    let storedStudentDetails = localStorage.getItem('student');
    try {
        storedStudentDetails = JSON.parse(storedStudentDetails);
    } catch (error) {
        console.log(error);
    }
    const studentId = storedStudentDetails._id;

    useEffect(() => {
        const fetchData = async (studentId) => {
            try {
                const response = await fetch(`http://localhost:4850/showEndrolledCourses/${studentId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await response.json();
                setEnrolledCourses(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData(studentId);
    }, []);

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap', alignItems: 'center' }}>
            {enrolledCourses.length > 0 ? (
                enrolledCourses.map((course) => (
                    <Courses
                        key={course._id}
                        id={course._id}
                        title={course.name}
                        description={course.description}
                        image={course.image}
                    />
                ))
            ) : (
                <Typography>No courses found</Typography>
            )}
        </div>
    );
};

const Courses = ({ id, title, description, image }) => {
    const navigate = useNavigate();

    return (
        <Card sx={{ maxWidth: 345 }}>
            <CardActionArea>
                <CardMedia
                    component="img"
                    height="140"
                    image={image}
                    alt={title}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {description}
                    </Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                <Button
                    size="small"
                    color="primary"
                    startIcon={<SendIcon />}
                    onClick={() => navigate('watch')}
                >
                    Start Watching
                </Button>
            </CardActions>
        </Card>
    );
};

export default CoursesRow;
