        {

        }
        catch(error)
        {
            console.log(error)
        }
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap', alignItems: 'center' }}>
            {enrolledCourses.length > 0 ? (
                enrolledCourses.map((course) => (
                    <Courses
                        key={course._id}
                        id={course._id}
                        title={course.name}
                        description={course.description}
                        image={course.image}
                        unEnroll={() => unEnrollCourse(course._id)}
                    />
                ))
            ) : (
                <Typography>No courses found</Typography>
            )}
        </div>
    );
};


const Courses = ({ id, title, description, image, unEnroll }) => {
    const navigate = useNavigate()
    return (
        <Card sx={{ maxWidth: 345 }}>
            <CardActionArea>
                <CardMedia
                    component="img"
                    height="140"
                    image={image}
                    alt={title}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {description}
                    </Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                <Button size="small" color="primary" startIcon={<SendIcon />} onClick={() => {
                    navigate('watch')
                }}>
                    Start Watching
                </Button>
            </CardActions>

            <CardActions>
                <Button size="small" color="primary" startIcon={<SendIcon />} onClick={unEnroll}>
                    unEnroll
                </Button>
            </CardActions>
        </Card>
    );
}

export default CoursesRow;
