import React, { useEffect, useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import SendIcon from '@mui/icons-material/Send';
import { Button, CardActionArea, CardActions } from '@mui/material';

const CoursesRow = () => {
    const [studEndrolledCourse, setStudEndrolledCourse] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:4850/showEndrolledCourses');
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await response.json();
                setStudEndrolledCourse(data.studEndrolledCourse);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap', alignItems: 'center' }}>
            {studEndrolledCourse.map(course => (
                <Courses
                    key={course._id}
                    id={course._id}
                    title={course.name}
                    description={course.description}
                    image={course.image}
                />
            ))}
        </div>
    );
};

const Courses = ({ id, title, description, image }) => {
    return (
        <Card sx={{ maxWidth: 345, margin: 5 }}>
            <CardActionArea>
                <CardMedia
                    component="img"
                    height="200"
                    image={image}
                    alt={title}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">
                        {title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        {description}
                    </Typography>
                </CardContent>
            </CardActionArea>
            <CardActions>
                <Button size="small" color="primary" startIcon={<SendIcon />}>
                    Enroll Now
                </Button>
            </CardActions>
        </Card>
    );
};

export default CoursesRow;
