import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import SendIcon from '@mui/icons-material/Send';
import Lectures from './Lectures';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'

import { Button, CardActionArea, CardActions } from '@mui/material';

// Container component to hold the cards in a row
const CoursesRow = () => {
    const navigate = useNavigate()
  return (
    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
      <Courses
        title="Web Design"
        description="Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging across all continents except Antarctica."
        image="https://www.google.com/imgres?imgurl=https%3A%2F%2Fnearlearn.com%2Fpublic%2Fimages%2Fpython-training-for-beginners.jpg&tbnid=tgmLr-IWuPBmQM&vet=12ahUKEwjNrZHww7CEAxWzTGwGHUcHB_sQMygbegUIARCuAQ..i&imgrefurl=https%3A%2F%2Fnearlearn.com%2Fpython-online-training-usa&docid=gaFrd5se0BCg2M&w=500&h=500&q=image%20for%20python%20course&ved=2ahUKEwjNrZHww7CEAxWzTGwGHUcHB_sQMygbegUIARCuAQ"
      />
      <Courses
        title="Data Science"
        description="Data science is an interdisciplinary field that uses scientific methods, processes, algorithms, and systems to extract knowledge and insights from structured and unstructured data."
        image="/Assets/ui-ux.png"
      />
      <Courses
        title="Mobile Development"
        description="Mobile app development is the act or process by which a mobile app is developed for mobile devices, such as personal digital assistants, enterprise digital assistants, or mobile phones."
        image="/Assets/graphics-design.png"
      />
    </div>
  );
};


const Courses = ({ title, description, image }) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image={image}
          alt={title}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
      <Button size="small" color="primary" startIcon={<SendIcon />} onClick={()=>{
        <BrowserRouter>
            <Routes>
                <Route path='/watch' element={<Lectures/>}/>
            </Routes>
        </BrowserRouter>
        navigate('/watch')
        
      }}>
          Start Watching
        </Button>
      </CardActions>
    </Card>
  );
}

export default CoursesRow;
